(()=>{"use strict";document.querySelector(".top-menu").querySelectorAll("ul>li>a").forEach((e=>{e.addEventListener("click",(e=>{e.preventDefault();const t=e.target.getAttribute("href").substr(1);document.getElementById(t).scrollIntoView({behavior:"smooth",block:"start"})}))})),(()=>{const e=document.querySelector(".btn-open"),t=document.querySelector(".modal-callback"),l=document.querySelector(".modal-overlay");e.addEventListener("click",(()=>{t.style.display="block",l.style.display="block"}))})()})();